    private void btnUserNameSaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnUserNameSaveActionPerformed

        String updateSql = "UPDATE logofemploye SET username = ? WHERE name = ?";
        try (PreparedStatement preparedStatement = conn.prepareStatement(updateSql)) {
            preparedStatement.setString(1, txtUserName.getText()); // UserName
            preparedStatement.setString(2, lblUserName.getText()); // Condition
            int rowsAffected = preparedStatement.executeUpdate();

        } catch (SQLException e) {
            e.printStackTrace();
        }
    }//GEN-LAST:event_btnUserNameSaveActionPerformed

