    private void btnPasswordSaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPasswordSaveActionPerformed
        String updateSql = "UPDATE logofemploye SET password = ? WHERE name = ?";
        if (password.getPassword() == paswordCheck.getPassword()) {
            try (PreparedStatement preparedStatement = conn.prepareStatement(updateSql)) {
                String UpdatePassword = new String(password.getPassword());
                if (PasswordUtils.isValidPassword(UpdatePassword)) {
                    preparedStatement.setString(1, PasswordUtils.hashPassword(UpdatePassword)); // Şifre hash'lenmiş hali 
                } else {
                    lblPasswordAgain.setText("The password must be at least 8 digits and contain one uppercase letter.");
                }
                preparedStatement.setString(2, lblUserName.getText()); // Condition
                int rowsAffected = preparedStatement.executeUpdate();

            } catch (SQLException e) {
                e.printStackTrace();
            }

        } else {
            lblPasswordAgain.setText("THE PASSWORDS ARE NOT THE SAME");
        }


    }//GEN-LAST:event_btnPasswordSaveActionPerformed

    private void btnPhoneSaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnPhoneSaveActionPerformed
    String updateSql = "UPDATE employee SET phoneNumber = ? WHERE name = ?";
    try (PreparedStatement preparedStatement = conn.prepareStatement(updateSql)) {
        preparedStatement.setString(1, txtPhone.getText()); // Phone
        preparedStatement.setString(2, lblUserName.getText()); // Condition
        int rowsAffected = preparedStatement.executeUpdate();
        
        if (rowsAffected > 0) {
            JOptionPane.showMessageDialog(this, "Telefon numarası başarıyla güncellendi.");
        } else {
            JOptionPane.showMessageDialog(this, "Telefon numarası güncellenirken bir hata oluştu.");
        }
    } catch (SQLException e) {
        JOptionPane.showMessageDialog(this, "Veritabanına erişim sırasında bir hata oluştu: " + e.getMessage());
        e.printStackTrace();
    }
    }//GEN-LAST:event_btnPhoneSaveActionPerformed

